import React, { useState, useEffect } from 'react';
import { 
  Package, TrendingUp, Users, IndianRupee, 
  Clock, AlertCircle, ArrowUp, ArrowDown 
} from 'lucide-react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import RevenueChart from './RevenueChart';
import RecentActivities from './RecentActivities';

const StatsOverview = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [dateRange, setDateRange] = useState('7');
  const navigate = useNavigate();

  useEffect(() => {
    const adminToken = localStorage.getItem('adminToken');
    const userType = localStorage.getItem('userType');
    
    if (!adminToken || userType !== 'admin') {
      navigate('/login');
      return;
    }
    
    fetchDashboardStats();
  }, [dateRange, navigate]);

  const fetchDashboardStats = async () => {
    try {
      setLoading(true);
      setError('');
      
      const adminToken = localStorage.getItem('adminToken');
      if (!adminToken) {
        throw new Error('No admin token found');
      }

      const endDate = new Date();
      const startDate = new Date();
      startDate.setDate(startDate.getDate() - parseInt(dateRange));

      const response = await axios.get('http://localhost:5000/api/admin/courier-stats', {
        headers: {
          'Authorization': `Bearer ${adminToken}`,
          'Content-Type': 'application/json'
        },
        params: {
          startDate: startDate.toISOString().split('T')[0],
          endDate: endDate.toISOString().split('T')[0]
        }
      });

      setStats(response.data);
    } catch (err) {
      console.error('Error fetching stats:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('adminToken');
        localStorage.removeItem('userType');
        navigate('/login');
      }
      setError(err.response?.data?.message || 'Failed to load dashboard statistics');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboardStats();
  }, [dateRange]);

  if (loading) {
    return (
      <div className="animate-pulse space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {[1, 2, 3, 4].map((i) => (
            <div key={i} className="bg-white p-6 rounded-lg shadow-sm">
              <div className="h-4 bg-gray-200 rounded w-1/3"></div>
              <div className="mt-4 h-8 bg-gray-200 rounded"></div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 p-4 rounded-lg">
        <div className="flex items-center">
          <AlertCircle className="h-5 w-5 text-red-500 mr-2" />
          <span className="text-red-700">{error}</span>
        </div>
      </div>
    );
  }

  if (!stats) return null;

  return (
    <div className="space-y-6">
      {/* Date Range Selector */}
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-900">Dashboard Overview</h1>
        <select
          value={dateRange}
          onChange={(e) => setDateRange(e.target.value)}
          className="block pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm rounded-md"
        >
          <option value="7">Last 7 days</option>
          <option value="30">Last 30 days</option>
          <option value="90">Last 3 months</option>
        </select>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {/* Total Shipments */}
        <div className="bg-white p-6 rounded-lg shadow-sm">
          <div className="flex items-center justify-between">
            <h3 className="text-sm font-medium text-gray-500">Total Shipments</h3>
            <Package className="h-5 w-5 text-blue-500" />
          </div>
          <div className="mt-2 flex items-baseline">
            <p className="text-2xl font-semibold text-gray-900">
              {stats.total_couriers || 0}
            </p>
            {stats.shipment_growth != null && (
              <p className={`ml-2 flex items-baseline text-sm font-semibold ${
                stats.shipment_growth >= 0 ? 'text-green-600' : 'text-red-600'
              }`}>
                {stats.shipment_growth >= 0 ? (
                  <ArrowUp className="h-4 w-4 mr-1" />
                ) : (
                  <ArrowDown className="h-4 w-4 mr-1" />
                )}
                {Math.abs(stats.shipment_growth)}%
              </p>
            )}
          </div>
          <p className="mt-1 text-sm text-gray-500">
            {stats.active_shipments || 0} active shipments
          </p>
        </div>

        {/* Revenue */}
        <div className="bg-white p-6 rounded-lg shadow-sm">
          <div className="flex items-center justify-between">
            <h3 className="text-sm font-medium text-gray-500">Total Revenue</h3>
            <IndianRupee className="h-5 w-5 text-green-500" />
          </div>
          <div className="mt-2 flex items-baseline">
            <p className="text-2xl font-semibold text-gray-900">
              ₹{(stats.total_revenue || 0).toLocaleString()}
            </p>
            {stats.revenue_growth != null && (
              <p className={`ml-2 flex items-baseline text-sm font-semibold ${
                stats.revenue_growth >= 0 ? 'text-green-600' : 'text-red-600'
              }`}>
                {stats.revenue_growth >= 0 ? (
                  <ArrowUp className="h-4 w-4 mr-1" />
                ) : (
                  <ArrowDown className="h-4 w-4 mr-1" />
                )}
                {Math.abs(stats.revenue_growth)}%
              </p>
            )}
          </div>
          <p className="mt-1 text-sm text-gray-500">
            Avg. ₹{((stats.total_revenue || 0) / (stats.total_couriers || 1)).toFixed(2)} per shipment
          </p>
        </div>

        {/* Delivery Performance */}
        <div className="bg-white p-6 rounded-lg shadow-sm">
          <div className="flex items-center justify-between">
            <h3 className="text-sm font-medium text-gray-500">Delivery Rate</h3>
            <TrendingUp className="h-5 w-5 text-purple-500" />
          </div>
          <div className="mt-2">
            <p className="text-2xl font-semibold text-gray-900">
              {(((stats.delivered || 0) / (stats.total_couriers || 1)) * 100).toFixed(1)}%
            </p>
          </div>
          <p className="mt-1 text-sm text-gray-500">
            {stats.delivered || 0} deliveries completed
          </p>
        </div>

        {/* Average Delivery Time */}
        <div className="bg-white p-6 rounded-lg shadow-sm">
          <div className="flex items-center justify-between">
            <h3 className="text-sm font-medium text-gray-500">Avg. Delivery Time</h3>
            <Clock className="h-5 w-5 text-orange-500" />
          </div>
          <div className="mt-2">
            <p className="text-2xl font-semibold text-gray-900">
              {stats.avg_delivery_time ? 
                `${Math.floor(stats.avg_delivery_time)} hrs` : 
                'N/A'}
            </p>
          </div>
          <p className="mt-1 text-sm text-gray-500">
            Target: 72 hours
          </p>
        </div>
      </div>

      {/* Charts and Recent Activity */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2">
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-lg font-semibold mb-4">Revenue Overview</h2>
            <RevenueChart dateRange={dateRange} />
          </div>
        </div>

        <div className="lg:col-span-1">
          <div className="bg-white rounded-lg shadow-sm p-6">
            <h2 className="text-lg font-semibold mb-4">Recent Activity</h2>
            <RecentActivities />
          </div>
        </div>
      </div>
    </div>
  );
};

export default StatsOverview;
