import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import { 
  Package, User, Mail, Phone, MapPin, 
  Upload, Calculator, AlertCircle, Loader 
} from 'lucide-react';

const CreateCourierForm = () => {
  const { user, getToken } = useAuth();
  const [formData, setFormData] = useState({
    semail: user?.email || '',
    remail: '',
    sname: user?.name || '',
    rname: '',
    sphone: user?.pnumber || '',
    rphone: '',
    saddress: user?.address || '',
    raddress: '',
    weight: '',
    image: null
  });

  const [price, setPrice] = useState(null);
  const [loading, setLoading] = useState(false);
  const [calculating, setCalculating] = useState(false);
  const [error, setError] = useState('');
  const [imagePreview, setImagePreview] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (formData.weight && parseFloat(formData.weight) > 0) {
      const debounceTimer = setTimeout(() => {
        calculatePrice();
      }, 500);

      return () => clearTimeout(debounceTimer);
    }
  }, [formData.weight]);

  const calculatePrice = async () => {
    try {
      setCalculating(true);
      const response = await fetch('http://localhost:5000/api/calculate-price', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          weight: parseFloat(formData.weight),
          zone: 'default'
        })
      });

      if (!response.ok) {
        throw new Error('Failed to calculate price');
      }

      const data = await response.json();
      setPrice(data.price);
    } catch (err) {
      console.error('Price calculation error:', err);
      setError('Failed to calculate price. Please try again.');
    } finally {
      setCalculating(false);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.size > 5 * 1024 * 1024) {
      setError('Image size must be less than 5MB');
      return;
    }

    if (!file.type.startsWith('image/')) {
      setError('Please upload an image file');
      return;
    }

    setFormData(prev => ({ ...prev, image: file }));
    setError('');

    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreview(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const formDataToSend = new FormData();
      Object.keys(formData).forEach(key => {
        if (key === 'image' && formData[key] instanceof File) {
          formDataToSend.append('image', formData[key]);
        } else if (formData[key] !== null && formData[key] !== '') {
          formDataToSend.append(key, formData[key]);
        }
      });

      const token = getToken();
      if (!token) {
        throw new Error('Please login to create a shipment');
      }

      const response = await fetch('http://localhost:5000/api/courier', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formDataToSend
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to create shipment');
      }

      navigate(`/courier/${data.courier.id}`);
    } catch (err) {
      console.error('Courier creation error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (error) setError('');
  };

  // Rest of your JSX code remains the same...
  
  return (
    <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-sm p-6">
      {/* Your existing JSX */}
    </div>
  );
};

export default CreateCourierForm;
