import { useState } from 'react';
import { courierService } from '../services/courierService';

export const useCourier = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const createShipment = async (data) => {
    setLoading(true);
    try {
      const response = await courierService.createCourier(data);
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const trackShipment = async (trackingNumber) => {
    setLoading(true);
    try {
      const response = await courierService.trackCourier(trackingNumber);
      return response;
    } catch (err) {
      setError(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return {
    loading,
    error,
    createShipment,
    trackShipment
  };
};
