import React, { createContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import toast from 'react-hot-toast';

export const AuthContext = createContext({
  user: null,
  loading: true,
  error: null,
  login: () => {},
  logout: () => {},
  register: () => {},
  updateProfile: () => {},
  isAuthenticated: false,
  isAdmin: false,
  clearError: () => {}
});

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    checkAuthStatus();
  }, [navigate]);

  const checkAuthStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      const storedUser = localStorage.getItem('user');
      
      if (!token || !storedUser) {
        setLoading(false);
        setUser(null);
        navigate('/login');
        return;
      }

      const parsedUser = JSON.parse(storedUser);
      
      const endpoint = parsedUser.isAdmin ? 
        'http://localhost:5000/api/admin/verify' : 
        'http://localhost:5000/api/user/profile';

      const response = await fetch(endpoint, {
        headers: {
          'Authorization': token.startsWith('Bearer ') ? token : `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Session expired');
      }

      const data = await response.json();
      
      if (data.user) {
        const updatedUser = { 
          ...parsedUser, 
          ...data.user,
          isAdmin: parsedUser.isAdmin // Preserve admin status
        };
        setUser(updatedUser);
        
        // Navigate based on verified admin status
        if (updatedUser.isAdmin) {
          navigate('/admin/dashboard');
        } else {
          navigate('/dashboard');
        }
      } else {
        setUser(parsedUser);
        if (parsedUser.isAdmin) {
          navigate('/admin/dashboard');
        } else {
          navigate('/dashboard');
        }
      }
    } catch (err) {
      console.error('Auth check failed:', err);
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      setUser(null);
      navigate('/login');
    } finally {
      setLoading(false);
    }
  };

  const login = async (credentials) => {
    try {
      setError(null);
      setLoading(true);

      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(credentials)
      });

      const data = await response.json();
      console.log('Login response:', data);

      if (!response.ok) {
        throw new Error(data.message || 'Login failed');
      }

      // Enhanced admin verification
      const isAdminUser = data.user && (
        data.user.isAdmin || 
        data.user.role === 'admin' || 
        data.user.type === 'admin'
      );

      const userData = {
        ...data.user,
        isAdmin: isAdminUser,
        type: isAdminUser ? 'admin' : 'user'
      };

      localStorage.setItem('token', `Bearer ${data.token}`);
      localStorage.setItem('user', JSON.stringify(userData));
      setUser(userData);

      toast.success('Login successful!');
      
      if (isAdminUser) {
        console.log('Admin login successful, redirecting to admin dashboard');
        navigate('/admin/dashboard', { replace: true });
      } else {
        console.log('User login successful, redirecting to user dashboard');
        navigate('/dashboard', { replace: true });
      }

      return { ...data, user: userData };
    } catch (err) {
      console.error('Login error:', err);
      setError(err.message);
      toast.error(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const register = async (userData) => {
    try {
      setError(null);
      setLoading(true);

      const formData = new FormData();
      Object.keys(userData).forEach(key => {
        if (userData[key] !== null && userData[key] !== undefined) {
          if (key === 'profileImage' && userData[key] instanceof File) {
            formData.append('profileImage', userData[key]);
          } else {
            formData.append(key, userData[key]);
          }
        }
      });

      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Registration failed');
      }

      toast.success('Registration successful! Please login.');
      navigate('/login', { 
        state: { 
          message: 'Registration successful! Please login.',
          type: 'success'
        }
      });

      return data;
    } catch (err) {
      setError(err.message);
      toast.error(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const updateProfile = async (profileData) => {
    try {
      setError(null);
      setLoading(true);

      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Authentication required');
      }

      const formData = new FormData();
      Object.keys(profileData).forEach(key => {
        if (profileData[key] !== null && profileData[key] !== undefined) {
          if (key === 'profileImage' && profileData[key] instanceof File) {
            formData.append('profileImage', profileData[key]);
          } else {
            formData.append(key, profileData[key]);
          }
        }
      });

      const endpoint = user?.isAdmin ?
        'http://localhost:5000/api/admin/profile' :
        'http://localhost:5000/api/user/profile';

      const response = await fetch(endpoint, {
        method: 'PUT',
        headers: {
          'Authorization': token.startsWith('Bearer ') ? token : `Bearer ${token}`,
        },
        body: formData
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to update profile');
      }

      const updatedUser = { 
        ...user, 
        ...data.user,
        isAdmin: user.isAdmin // Preserve admin status
      };
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      toast.success('Profile updated successfully');

      return data;
    } catch (err) {
      setError(err.message);
      toast.error(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    const wasAdmin = user?.isAdmin;
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    toast.success('Logged out successfully');
    navigate('/login', { state: { from: wasAdmin ? '/admin' : '/' } });
  };

  const clearError = () => {
    setError(null);
  };

  const value = {
    user,
    loading,
    error,
    login,
    logout,
    register,
    updateProfile,
    clearError,
    isAuthenticated: !!user,
    isAdmin: user?.isAdmin || false
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = React.useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export default AuthProvider;
