// src/components/common/Sidebar.jsx
import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import {
  Home,
  Package,
  Users,
  Settings,
  Activity,
  TrendingUp,
  FileText,
  Box,
  User,
  CreditCard,
  BarChart2,
  MessageSquare,
  Map
} from 'lucide-react';

const Sidebar = () => {
  const { user } = useAuth();
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  const adminMenuItems = [
    { 
      section: 'Main',
      items: [
        { path: '/admin', icon: Home, label: 'Dashboard' },
        { path: '/admin/courier/list', icon: Package, label: 'Shipments' },
        { path: '/admin/users', icon: Users, label: 'Users' }
      ]
    },
    {
      section: 'Management',
      items: [
        { path: '/admin/pricing', icon: CreditCard, label: 'Pricing' },
        { path: '/admin/contacts', icon: MessageSquare, label: 'Support' },
        { path: '/admin/reports', icon: BarChart2, label: 'Reports' }
      ]
    },
    {
      section: 'System',
      items: [
        { path: '/admin/logs', icon: Activity, label: 'Activity Logs' },
        { path: '/admin/settings', icon: Settings, label: 'Settings' }
      ]
    }
  ];

  const userMenuItems = [
    {
      section: 'Shipments',
      items: [
        { path: '/', icon: Home, label: 'Dashboard' },
        { path: '/create-courier', icon: Box, label: 'New Shipment' },
        { path: '/courier/list', icon: Package, label: 'My Shipments' },
        { path: '/track', icon: Map, label: 'Track Shipment' }
      ]
    },
    {
      section: 'Account',
      items: [
        { path: '/profile', icon: User, label: 'Profile' },
        { path: '/notifications', icon: Activity, label: 'Notifications' }
      ]
    }
  ];

  const menuItems = user?.isAdmin ? adminMenuItems : userMenuItems;

  return (
    <aside className="fixed inset-y-0 left-0 w-64 bg-white border-r border-gray-200 pt-16 pb-4 overflow-y-auto">
      <div className="h-full flex flex-col justify-between">
        <nav className="flex-1 px-3 space-y-6">
          {menuItems.map((section, index) => (
            <div key={index}>
              <h3 className="px-3 text-xs font-semibold text-gray-500 uppercase tracking-wider">
                {section.section}
              </h3>
              <div className="mt-2 space-y-1">
                {section.items.map((item) => (
                  <Link
                    key={item.path}
                    to={item.path}
                    className={`flex items-center px-3 py-2 rounded-md transition-colors ${
                      isActive(item.path)
                        ? 'bg-blue-50 text-blue-600'
                        : 'text-gray-700 hover:bg-gray-50'
                    }`}
                  >
                    <item.icon className="h-5 w-5 mr-3" />
                    <span className="text-sm font-medium">{item.label}</span>
                  </Link>
                ))}
              </div>
            </div>
          ))}
        </nav>

        {/* Version Info */}
        <div className="px-3 py-4">
          <div className="px-3">
            <div className="text-xs text-gray-500">
              <p>FastCourier v1.0.0</p>
              <p className="mt-1">© 2024 All rights reserved</p>
            </div>
          </div>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
