import React, { createContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import toast from 'react-hot-toast';

export const AuthContext = createContext({
  user: null,
  loading: true,
  error: null,
  login: () => {},
  logout: () => {},
  register: () => {},
  updateProfile: () => {},
  isAuthenticated: false,
  isAdmin: false,
  clearError: () => {}
});

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Initial navigation check
  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');
    
    // Redirect to login if no auth data
    if (!token || !storedUser) {
      setUser(null);
      setLoading(false);
      navigate('/login');
      return;
    }

    // Initialize user state from storage
    try {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);
      setLoading(false);
    } catch (error) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      setUser(null);
      setLoading(false);
      navigate('/login');
    }
  }, []);

  // Auth status check
  useEffect(() => {
    if (user) {
      checkAuthStatus();
    }
  }, [user]);

  const checkAuthStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      const storedUser = localStorage.getItem('user');
      
      if (!token || !storedUser) {
        setUser(null);
        navigate('/login');
        return;
      }

      try {
        const parsedUser = JSON.parse(storedUser);
        const authToken = token.startsWith('Bearer ') ? token : `Bearer ${token}`;

        if (!parsedUser.isAdmin) {
          const response = await fetch('http://localhost:5000/api/user/profile', {
            headers: {
              'Authorization': authToken,
              'Content-Type': 'application/json'
            }
          });

          if (!response.ok) {
            throw new Error('Invalid session');
          }

          const data = await response.json();
          setUser({ ...parsedUser, ...data.user });
          navigate('/dashboard');
        } else {
          setUser(parsedUser);
          navigate('/admin/dashboard');
        }
      } catch (error) {
        throw new Error('Session verification failed');
      }
    } catch (err) {
      console.error('Auth check failed:', err);
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      setUser(null);
      navigate('/login');
    } finally {
      setLoading(false);
    }
  };

  const login = async (credentials) => {
    try {
      setError(null);
      setLoading(true);

      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(credentials)
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Login failed');
      }

      const isAdminUser = data.user.isAdmin || data.user.type === 'admin';
      const userData = {
        ...data.user,
        isAdmin: isAdminUser,
        type: isAdminUser ? 'admin' : 'user'
      };

      localStorage.setItem('token', `Bearer ${data.token}`);
      localStorage.setItem('user', JSON.stringify(userData));
      setUser(userData);

      toast.success('Login successful!');

      if (isAdminUser) {
        navigate('/admin/dashboard', { replace: true });
      } else {
        navigate('/dashboard', { replace: true });
      }

      return data;
    } catch (err) {
      setError(err.message);
      toast.error(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const register = async (userData) => {
    try {
      setError(null);
      setLoading(true);

      const formData = new FormData();
      Object.keys(userData).forEach(key => {
        if (userData[key] !== null && userData[key] !== undefined) {
          if (key === 'profileImage' && userData[key] instanceof File) {
            formData.append('profileImage', userData[key]);
          } else {
            formData.append(key, userData[key]);
          }
        }
      });

      const response = await fetch('http://localhost:5000/api/register', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Registration failed');
      }

      toast.success('Registration successful! Please login.');
      navigate('/login', { 
        state: { 
          message: 'Registration successful! Please login.',
          type: 'success'
        }
      });

      return data;
    } catch (err) {
      setError(err.message);
      toast.error(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const updateProfile = async (profileData) => {
    try {
      setError(null);
      setLoading(true);

      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Authentication required');
      }

      const formData = new FormData();
      Object.keys(profileData).forEach(key => {
        if (profileData[key] !== null && profileData[key] !== undefined) {
          if (key === 'profileImage' && profileData[key] instanceof File) {
            formData.append('profileImage', profileData[key]);
          } else {
            formData.append(key, profileData[key]);
          }
        }
      });

      const endpoint = user?.isAdmin ?
        'http://localhost:5000/api/admin/profile' :
        'http://localhost:5000/api/user/profile';

      const response = await fetch(endpoint, {
        method: 'PUT',
        headers: {
          'Authorization': token
        },
        body: formData
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to update profile');
      }

      const updatedUser = { ...user, ...data.user };
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      toast.success('Profile updated successfully');

      return data;
    } catch (err) {
      setError(err.message);
      toast.error(err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    toast.success('Logged out successfully');
    navigate('/login');
  };

  const clearError = () => {
    setError(null);
  };

  const value = {
    user,
    loading,
    error,
    login,
    logout,
    register,
    updateProfile,
    clearError,
    isAuthenticated: !!user,
    isAdmin: user?.isAdmin || false
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = React.useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export default AuthProvider;
