import React from 'react';
import { Toaster } from 'react-hot-toast';
import { Routes, Route, Navigate, useLocation, BrowserRouter } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import { NotificationProvider } from './context/NotificationContext';

// Layout Components
import MainLayout from './components/layouts/MainLayout';
import AdminLayout from './components/layouts/AdminLayout';
import AuthLayout from './components/layouts/AuthLayout';

// Auth Components
import LoginForm from './components/auth/LoginForm';
import RegisterForm from './components/auth/RegisterForm';

// User Components
import Dashboard from './components/user/Dashboard';
import Profile from './components/user/Profile';
import Notifications from './components/user/Notifications';

// Courier Components
import CreateCourierForm from './components/courier/CreateCourierForm';
import CourierList from './components/courier/CourierList';
import CourierDetails from './components/courier/CourierDetails';
import TrackingHistory from './components/courier/TrackingHistory';
import TrackShipment from './components/courier/TrackShipment';
import PriceCalculator from './components/courier/PriceCalculator';

// Admin Components
import AdminDashboard from './components/admin/dashboard/AdminDashboard';
import StatsOverview from './components/admin/dashboard/StatsOverview';
import CourierManagement from './components/admin/courier/CourierManagement';
import UserManagement from './components/admin/users/UserManagement';
import PricingManagement from './components/admin/pricing/PricingManagement';
import ActivityLogs from './components/admin/reports/ActivityLogs';
import PerformanceMetrics from './components/admin/reports/PerformanceMetrics';
//import SystemSettings from './components/admin/settings/SystemSettings';

// Loading Spinner Component
const LoadingSpinner = () => (
  <div className="min-h-screen flex items-center justify-center">
    <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-600 border-t-transparent"></div>
  </div>
);

// Protected Route Components
const ProtectedRoute = ({ children, requireAdmin = false }) => {
  const { user, loading } = useAuth();
  const location = useLocation();

  if (loading) {
    return <LoadingSpinner />;
  }

  if (!user) {
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  if (requireAdmin && !user.isAdmin) {
    return <Navigate to="/dashboard" replace />;
  }

  return children;
};

const PublicRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return <LoadingSpinner />;
  }

  if (user) {
    return <Navigate to={user.isAdmin ? '/admin/dashboard' : '/dashboard'} replace />;
  }

  return children;
};

// Main Routes Component
const AppRoutes = () => {
  const { user } = useAuth();

  return (
    <Routes>
      {/* Public Routes */}
      <Route element={<AuthLayout />}>
        <Route
          path="/login"
          element={
            <PublicRoute>
              <LoginForm />
            </PublicRoute>
          }
        />
        <Route
          path="/register"
          element={
            <PublicRoute>
              <RegisterForm />
            </PublicRoute>
          }
        />
      </Route>

      {/* Public Tracking Route */}
      <Route path="/track/:trackingNumber" element={<TrackShipment />} />

      {/* Protected User Routes */}
      <Route
        element={
          <ProtectedRoute>
            <MainLayout />
          </ProtectedRoute>
        }
      >
        <Route index element={<Dashboard />} />
        <Route path="dashboard" element={<Dashboard />} />
        <Route path="profile" element={<Profile />} />
        <Route path="notifications" element={<Notifications />} />
        <Route path="create-courier" element={<CreateCourierForm />} />
        <Route path="couriers" element={<CourierList />} />
        <Route path="courier/:id" element={<CourierDetails />} />
        <Route path="tracking/:id" element={<TrackingHistory />} />
        <Route path="calculate-price" element={<PriceCalculator />} />
      </Route>

      {/* Protected Admin Routes */}
      <Route
        path="admin"
        element={
          <ProtectedRoute requireAdmin>
            <AdminLayout />
          </ProtectedRoute>
        }
      >
        <Route index element={<AdminDashboard />} />
        <Route path="dashboard" element={<AdminDashboard />} />
        <Route path="stats" element={<StatsOverview />} />
        <Route path="couriers" element={<CourierManagement />} />
        <Route path="users" element={<UserManagement />} />
        <Route path="pricing" element={<PricingManagement />} />
        <Route path="logs" element={<ActivityLogs />} />
        <Route path="metrics" element={<PerformanceMetrics />} />
        <Route path="profile" element={<Profile />} />
        <Route path="notifications" element={<Notifications />} />
      </Route>

      {/* Root Redirect */}
      <Route
        path="/"
        element={
          user ? (
            <Navigate to={user.isAdmin ? "/admin/dashboard" : "/dashboard"} replace />
          ) : (
            <Navigate to="/login" replace />
          )
        }
      />

      {/* 404 Route */}
      <Route path="*" element={
        <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50">
          <div className="text-center">
            <h1 className="text-6xl font-bold text-gray-900 mb-4">404</h1>
            <p className="text-xl text-gray-600 mb-8">Page not found</p>
            <button
              onClick={() => window.history.back()}
              className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors duration-200"
            >
              Go Back
            </button>
          </div>
        </div>
      } />
    </Routes>
  );
};

// Main App Component
const App = () => {
  return (
    <BrowserRouter>
      <AuthProvider>
        <NotificationProvider>
          <div className="min-h-screen bg-gray-50">
            <Toaster
              position="top-right"
              toastOptions={{
                duration: 4000,
                style: {
                  background: '#363636',
                  color: '#fff',
                },
                success: {
                  duration: 3000,
                  theme: {
                    primary: '#4aed88',
                  },
                },
              }}
            />
            <AppRoutes />
          </div>
        </NotificationProvider>
      </AuthProvider>
    </BrowserRouter>
  );
};

export default App;
