const Select = ({ children, value, onValueChange, className, ...props }) => (
    <select
      value={value}
      onChange={(e) => onValueChange?.(e.target.value)}
      className={`w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent ${className}`}
      {...props}
    >
      {children}
    </select>
  );
  
  const SelectTrigger = ({ className, children, ...props }) => (
    <div className={`relative ${className}`} {...props}>
      {children}
    </div>
  );
  
  const SelectValue = ({ placeholder }) => placeholder;
  
  const SelectContent = ({ children }) => children;
  
  const SelectItem = ({ value, children, ...props }) => (
    <option value={value} {...props}>
      {children}
    </option>
  );
  
  export { Select, SelectTrigger, SelectValue, SelectContent, SelectItem };
