// src/hooks/useCourier.js
import { useState } from 'react';
import { useAuth } from './useAuth';
import api from '../services/api';
import toast from 'react-hot-toast';

export const useCourier = () => {
  const [couriers, setCouriers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const { user } = useAuth();

  const fetchCouriers = async (filters = {}) => {
    try {
      setLoading(true);
      setError(null);

      const endpoint = user?.isAdmin ? '/admin/couriers' : '/courier';
      const { data } = await api.get(endpoint, { params: filters });

      setCouriers(data);
    } catch (err) {
      console.error('Fetch couriers error:', err);
      setError('Failed to fetch couriers');
      toast.error('Failed to fetch couriers');
    } finally {
      setLoading(false);
    }
  };

  const updateTracking = async (courierId, trackingData) => {
    try {
      setLoading(true);
      setError(null);

      const { data } = await api.post('/admin/tracking-update', {
        courierId,
        ...trackingData
      });

      toast.success('Tracking status updated successfully');
      return data;
    } catch (err) {
      console.error('Update tracking error:', err);
      setError('Failed to update tracking status');
      toast.error('Failed to update tracking status');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const createCourier = async (courierData) => {
    try {
      setLoading(true);
      setError(null);

      const formData = new FormData();
      Object.keys(courierData).forEach(key => {
        if (courierData[key] !== null && courierData[key] !== undefined) {
          formData.append(key, courierData[key]);
        }
      });

      const { data } = await api.post('/courier', formData);
      toast.success('Courier created successfully');
      return data;
    } catch (err) {
      console.error('Create courier error:', err);
      setError('Failed to create courier');
      toast.error('Failed to create courier');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const getCourierDetails = async (id) => {
    try {
      setLoading(true);
      setError(null);

      const { data } = await api.get(`/courier/${id}`);
      return data;
    } catch (err) {
      console.error('Get courier details error:', err);
      setError('Failed to fetch courier details');
      toast.error('Failed to fetch courier details');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const calculatePrice = async (weight, zone = 'default') => {
    try {
      setLoading(true);
      setError(null);

      const { data } = await api.post('/calculate-price', { weight, zone });
      return data.price;
    } catch (err) {
      console.error('Calculate price error:', err);
      setError('Failed to calculate price');
      toast.error('Failed to calculate price');
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return {
    couriers,
    loading,
    error,
    fetchCouriers,
    updateTracking,
    createCourier,
    getCourierDetails,
    calculatePrice
  };
};

export default useCourier;
