// src/components/admin/reports/PerformanceMetrics.jsx
import React, { useState, useEffect } from 'react';
import { 
  TrendingUp, Clock, AlertCircle, Download, 
  IndianRupee, Package
} from 'lucide-react';
import { 
  LineChart, Line, BarChart, Bar, XAxis, YAxis, 
  CartesianGrid, Tooltip, Legend, ResponsiveContainer 
} from 'recharts';
import axios from 'axios';

const MetricCard = ({ title, value, subtitle, icon: Icon, trend }) => (
  <div className="bg-white rounded-lg shadow-sm p-6">
    <div className="flex items-center justify-between">
      <h3 className="text-sm font-medium text-gray-500">{title}</h3>
      <Icon className={`h-5 w-5 ${trend >= 0 ? 'text-green-500' : 'text-red-500'}`} />
    </div>
    <div className="mt-2">
      <p className="text-3xl font-bold text-gray-900">{value}</p>
      {subtitle && (
        <p className="mt-1 text-sm text-gray-500">{subtitle}</p>
      )}
      {trend !== undefined && (
        <p className={`mt-1 text-sm ${trend >= 0 ? 'text-green-600' : 'text-red-600'}`}>
          {trend >= 0 ? '↑' : '↓'} {Math.abs(trend)}% from last period
        </p>
      )}
    </div>
  </div>
);

const PerformanceMetrics = () => {
  const [metrics, setMetrics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [dateRange, setDateRange] = useState('30');
  const [view, setView] = useState('overview');

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem('adminToken');
        
        const response = await axios.get(`http://localhost:5000/api/admin/performance-metrics`, {
          headers: { 'Authorization': `Bearer ${token}` },
          params: { range: dateRange }
        });

        setMetrics(response.data);
      } catch (err) {
        console.error('Error fetching metrics:', err);
        setError(err.response?.data?.message || 'Failed to fetch metrics');
      } finally {
        setLoading(false);
      }
    };

    fetchMetrics();
  }, [dateRange]);

  const handleExport = async () => {
    try {
      const token = localStorage.getItem('adminToken');
      const response = await axios.get(
        `http://localhost:5000/api/admin/performance-metrics/export`,
        {
          headers: { 
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          params: { range: dateRange },
          responseType: 'blob'
        }
      );

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `performance-metrics-${new Date().toISOString().split('T')[0]}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error('Export error:', err);
    }
  };

  if (loading) {
    return (
      <div className="animate-pulse space-y-8">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {[1, 2, 3, 4].map((i) => (
            <div key={i} className="h-32 bg-gray-200 rounded-lg"></div>
          ))}
        </div>
        <div className="h-96 bg-gray-200 rounded-lg"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 p-4 rounded-md">
        <div className="flex items-center">
          <AlertCircle className="h-5 w-5 text-red-500 mr-2" />
          <span className="text-red-700">{error}</span>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-900">Performance Metrics</h1>
        <div className="flex items-center gap-4">
          <select
            value={dateRange}
            onChange={(e) => setDateRange(e.target.value)}
            className="block w-40 pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm rounded-md"
          >
            <option value="7">Last 7 days</option>
            <option value="30">Last 30 days</option>
            <option value="90">Last 3 months</option>
            <option value="365">Last year</option>
          </select>
          <button
            onClick={handleExport}
            className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
          >
            <Download className="h-4 w-4 mr-2" />
            Export Report
          </button>
        </div>
      </div>

      {/* Overview Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <MetricCard
          title="Success Rate"
          value={`${metrics?.successRate.toFixed(1)}%`}
          subtitle={`${metrics?.deliveredCount} of ${metrics?.totalShipments} delivered`}
          icon={TrendingUp}
          trend={metrics?.successRateGrowth}
        />
        <MetricCard
          title="Avg Delivery Time"
          value={`${metrics?.avgDeliveryTime.toFixed(1)} hrs`}
          subtitle="Target: 72 hours"
          icon={Clock}
          trend={-metrics?.deliveryTimeGrowth}
        />
        <MetricCard
          title="Average Revenue"
          value={`₹${metrics?.avgRevenue.toFixed(2)}`}
          subtitle={`Total: ₹${metrics?.totalRevenue.toLocaleString()}`}
          icon={IndianRupee}
          trend={metrics?.revenueGrowth}
        />
        <MetricCard
          title="Failed Deliveries"
          value={metrics?.failedCount}
          subtitle={`${((metrics?.failedCount / metrics?.totalShipments) * 100).toFixed(1)}% of total`}
          icon={AlertCircle}
          trend={-metrics?.failureRateGrowth}
        />
      </div>

      {/* Delivery Performance Chart */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="bg-white rounded-lg shadow-sm p-6">
          <h3 className="text-lg font-medium text-gray-900 mb-6">Delivery Performance</h3>
          <div className="h-80">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={metrics?.deliveryTrends}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis yAxisId="left" />
                <YAxis yAxisId="right" orientation="right" />
                <Tooltip />
                <Legend />
                <Line
                  yAxisId="left"
                  type="monotone"
                  dataKey="successRate"
                  stroke="#10B981"
                  name="Success Rate (%)"
                />
                <Line
                  yAxisId="right"
                  type="monotone"
                  dataKey="avgDeliveryTime"
                  stroke="#6366F1"
                  name="Delivery Time (hrs)"
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Revenue Trends */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          <h3 className="text-lg font-medium text-gray-900 mb-6">Revenue Analysis</h3>
          <div className="h-80">
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={metrics?.revenueTrends}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis yAxisId="left" />
                <YAxis yAxisId="right" orientation="right" />
                <Tooltip />
                <Legend />
                <Bar
                  yAxisId="left"
                  dataKey="revenue"
                  fill="#8B5CF6"
                  name="Revenue (₹)"
                />
                <Bar
                  yAxisId="right"
                  dataKey="shipments"
                  fill="#60A5FA"
                  name="Shipments"
                />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>

      {/* Zone Analysis */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-6">Zone Performance</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {metrics?.zonePerformance?.map((zone) => (
            <div key={zone.name} className="bg-gray-50 p-4 rounded-lg">
              <div className="flex justify-between items-center mb-2">
                <span className="text-sm font-medium text-gray-900">{zone.name}</span>
                <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                  zone.performance >= 90 ? 'bg-green-100 text-green-800' :
                  zone.performance >= 75 ? 'bg-yellow-100 text-yellow-800' :
                  'bg-red-100 text-red-800'
                }`}>
                  {zone.performance}% Success
                </span>
              </div>
              <div className="space-y-1 text-sm text-gray-500">
                <div className="flex justify-between">
                  <span>Avg. Delivery Time</span>
                  <span>{zone.avgDeliveryTime} hrs</span>
                </div>
                <div className="flex justify-between">
                  <span>Total Revenue</span>
                  <span>₹{zone.revenue.toLocaleString()}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Status Distribution */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-6">Status Distribution</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {Object.entries(metrics?.statusDistribution || {}).map(([status, count]) => (
            <div key={status} className="bg-gray-50 p-4 rounded-lg">
              <div className="flex justify-between items-center">
                <span className="text-sm text-gray-700">{status}</span>
                <span className="text-sm font-medium text-gray-900">{count}</span>
              </div>
              <div className="mt-2 w-full bg-gray-200 rounded-full h-2">
                <div
                  className="bg-blue-600 h-2 rounded-full"
                  style={{
                    width: `${(count / metrics?.totalShipments) * 100}%`
                  }}
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PerformanceMetrics;
