import React, { Suspense } from 'react';
import { Routes, Route, Navigate, useLocation, BrowserRouter } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import { NotificationProvider } from './context/NotificationContext';
import { Toaster } from 'react-hot-toast';

// Layout Components
import MainLayout from './components/layouts/MainLayout';
import AdminLayout from './components/layouts/AdminLayout';
import AuthLayout from './components/layouts/AuthLayout';

// Lazy load components for better performance
const LoginForm = React.lazy(() => import('./components/auth/LoginForm'));
const RegisterForm = React.lazy(() => import('./components/auth/RegisterForm'));
const Dashboard = React.lazy(() => import('./components/user/Dashboard'));
const Profile = React.lazy(() => import('./components/user/Profile'));
const Notifications = React.lazy(() => import('./components/user/Notifications'));
const CreateCourierForm = React.lazy(() => import('./components/courier/CreateCourierForm'));
const CourierList = React.lazy(() => import('./components/courier/CourierList'));
const CourierDetails = React.lazy(() => import('./components/courier/CourierDetails'));
const TrackingHistory = React.lazy(() => import('./components/courier/TrackingHistory'));
const PriceCalculator = React.lazy(() => import('./components/courier/PriceCalculator'));
const StatsOverview = React.lazy(() => import('./components/admin/dashboard/StatsOverview'));
const CourierManagement = React.lazy(() => import('./components/admin/courier/CourierManagement'));
const UserManagement = React.lazy(() => import('./components/admin/users/UserManagement'));
const PricingManagement = React.lazy(() => import('./components/admin/pricing/PricingManagement'));
const ActivityLogs = React.lazy(() => import('./components/admin/reports/ActivityLogs'));
const PerformanceMetrics = React.lazy(() => import('./components/admin/reports/PerformanceMetrics'));

const LoadingSpinner = () => (
  <div className="min-h-screen flex items-center justify-center bg-gray-50">
    <div className="flex flex-col items-center gap-4">
      <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-600 border-t-transparent"></div>
      <p className="text-gray-600">Loading...</p>
    </div>
  </div>
);

const ProtectedRoute = ({ children, allowedRoles = [] }) => {
  const { user, loading, isAuthenticated } = useAuth();
  const location = useLocation();

  if (loading) {
    return <LoadingSpinner />;
  }

  if (!isAuthenticated) {
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  if (allowedRoles.length > 0 && !allowedRoles.includes(user.role)) {
    return <Navigate to="/" replace />;
  }

  return <Suspense fallback={<LoadingSpinner />}>{children}</Suspense>;
};

const PublicRoute = ({ children }) => {
  const { isAuthenticated, user, loading } = useAuth();

  if (loading) {
    return <LoadingSpinner />;
  }

  if (isAuthenticated) {
    return <Navigate to={user?.isAdmin ? '/admin' : '/'} replace />;
  }

  return <Suspense fallback={<LoadingSpinner />}>{children}</Suspense>;
};

const AppRoutes = () => {
  const { user } = useAuth();

  return (
    <Routes>
      {/* Public Routes */}
      <Route element={<AuthLayout />}>
        <Route
          path="/login"
          element={
            <PublicRoute>
              <LoginForm />
            </PublicRoute>
          }
        />
        <Route
          path="/register"
          element={
            <PublicRoute>
              <RegisterForm />
            </PublicRoute>
          }
        />
      </Route>

      {/* Protected User Routes */}
      <Route
        element={
          <ProtectedRoute>
            <MainLayout />
          </ProtectedRoute>
        }
      >
        <Route index element={<Dashboard />} />
        <Route path="dashboard" element={<Dashboard />} />
        <Route path="profile" element={<Profile />} />
        <Route path="notifications" element={<Notifications />} />
        <Route path="create-courier" element={<CreateCourierForm />} />
        <Route path="couriers" element={<CourierList />} />
        <Route path="courier/:id" element={<CourierDetails />} />
        <Route path="tracking/:id" element={<TrackingHistory />} />
        <Route path="calculate-price" element={<PriceCalculator />} />
      </Route>

      {/* Protected Admin Routes */}
      <Route
        element={
          <ProtectedRoute allowedRoles={['admin', 'super_admin']}>
            <AdminLayout />
          </ProtectedRoute>
        }
      >
        <Route path="admin" element={<StatsOverview />} />
        <Route path="admin/dashboard" element={<StatsOverview />} />
        <Route path="admin/couriers" element={<CourierManagement />} />
        <Route path="admin/users" element={<UserManagement />} />
        <Route path="admin/pricing" element={<PricingManagement />} />
        <Route path="admin/logs" element={<ActivityLogs />} />
        <Route path="admin/metrics" element={<PerformanceMetrics />} />
      </Route>

      {/* Root Redirect */}
      <Route
        path="/"
        element={
          user ? (
            <Navigate to={user.isAdmin ? "/admin/dashboard" : "/dashboard"} replace />
          ) : (
            <Navigate to="/login" replace />
          )
        }
      />

      {/* 404 Route */}
      <Route
        path="*"
        element={
          <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50">
            <h1 className="text-6xl font-bold text-gray-800 mb-4">404</h1>
            <p className="text-xl text-gray-600 mb-8">Page not found</p>
            <button
              onClick={() => window.history.back()}
              className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              Go Back
            </button>
          </div>
        }
      />
    </Routes>
  );
};

const App = () => {
  return (
    <BrowserRouter>
      <AuthProvider>
        <NotificationProvider>
          <div className="min-h-screen bg-gray-50">
            <AppRoutes />
            <Toaster position="top-right" />
          </div>
        </NotificationProvider>
      </AuthProvider>
    </BrowserRouter>
  );
};

export default App;
