// src/hooks/useCourier.js
import { useState, useCallback } from 'react';
import { useAuth } from './useAuth';
import api from '../services/api';
import toast from 'react-hot-toast';

export const useCourier = () => {
  const [couriers, setCouriers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const { user } = useAuth();

  const fetchCouriers = useCallback(async (filters = {}) => {
    try {
      setLoading(true);
      setError(null);

      const endpoint = user?.isAdmin ? '/admin/couriers' : '/courier';
      const { data } = await api.get(endpoint, { params: filters });

      setCouriers(data);
    } catch (err) {
      console.error('Fetch couriers error:', err);
      setError('Failed to fetch couriers');
      toast.error('Failed to fetch couriers');
    } finally {
      setLoading(false);
    }
  }, [user?.isAdmin]);

  const updateTracking = async (courierId, trackingData) => {
    try {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Authentication required');
      }

      const response = await fetch('http://localhost:5000/api/admin/tracking-update', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': token
        },
        body: JSON.stringify({
          courierId,
          ...trackingData
        })
      });

      const data = await response.json();

      if (!response.ok || !data.success) {
        throw new Error(data.message || 'Failed to update tracking');
      }

      await fetchCouriers();
      return data;
    } catch (err) {
      console.error('Update tracking error:', err);
      throw new Error(err.message || 'Failed to update tracking');
    } finally {
      setLoading(false);
    }
  };

  const createCourier = useCallback(async (courierData) => {
    try {
      setLoading(true);
      setError(null);

      const formData = new FormData();
      Object.keys(courierData).forEach(key => {
        if (courierData[key] !== null && courierData[key] !== undefined) {
          if (key === 'image' && courierData[key] instanceof File) {
            formData.append('image', courierData[key]);
          } else {
            formData.append(key, courierData[key]);
          }
        }
      });

      const { data } = await api.post('/courier', formData);
      toast.success('Courier created successfully');
      await fetchCouriers();
      return data;
    } catch (err) {
      console.error('Create courier error:', err);
      setError('Failed to create courier');
      toast.error('Failed to create courier');
      throw err;
    } finally {
      setLoading(false);
    }
  }, [fetchCouriers]);

  const getCourierDetails = useCallback(async (id) => {
    try {
      setLoading(true);
      setError(null);

      const endpoint = user?.isAdmin ? `/admin/courier/${id}` : `/courier/${id}`;
      const { data } = await api.get(endpoint);
      return data;
    } catch (err) {
      console.error('Get courier details error:', err);
      setError('Failed to fetch courier details');
      toast.error('Failed to fetch courier details');
      throw err;
    } finally {
      setLoading(false);
    }
  }, [user?.isAdmin]);

  const calculatePrice = useCallback(async (weight, zone = 'default') => {
    try {
      setLoading(true);
      setError(null);

      const { data } = await api.post('/calculate-price', { weight, zone });
      return data.price;
    } catch (err) {
      console.error('Calculate price error:', err);
      setError('Failed to calculate price');
      toast.error('Failed to calculate price');
      throw err;
    } finally {
      setLoading(false);
    }
  }, []);

  return {
    couriers,
    loading,
    error,
    fetchCouriers,
    updateTracking,
    createCourier,
    getCourierDetails,
    calculatePrice,
    clearError: () => setError(null)
  };
};

export default useCourier;
