import React, { useState } from 'react';
import { Calculator, AlertCircle, Package, IndianRupee, Clock, Info } from 'lucide-react';
import toast from 'react-hot-toast';
import { useAuth } from '../../hooks/useAuth';

const PriceCalculator = () => {
  const { token } = useAuth();
  const [formData, setFormData] = useState({
    weight: '',
    zone: 'default'
  });
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const deliveryTimes = {
    default: '3-5 business days',
    express: '1-2 business days',
    international: '7-14 business days'
  };

  const validateInput = () => {
    const weight = parseFloat(formData.weight);
    if (!weight || weight <= 0) {
      setError('Please enter a valid weight');
      toast.error('Please enter a valid weight');
      return false;
    }
    if (weight > 100) { // Increased max weight limit
      setError('Maximum weight allowed is 100 kg');
      toast.error('Maximum weight allowed is 100 kg');
      return false;
    }
    return true;
  };

  const calculatePrice = async () => {
    if (!validateInput()) return;

    setLoading(true);
    setError('');

    try {
      // Convert weight to number and ensure it's positive
      const weight = Math.max(0, parseFloat(formData.weight));

      const response = await fetch('http://localhost:5000/api/calculate-price', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          weight: weight,
          zone: formData.zone
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to calculate price');
      }

      if (!data.price && data.price !== 0) {
        throw new Error('Invalid price calculation');
      }

      // Apply zone multipliers
      let finalPrice = data.price;
      if (formData.zone === 'express') {
        finalPrice *= 1.5; // 50% more for express
      } else if (formData.zone === 'international') {
        finalPrice *= 2.5; // 150% more for international
      }

      // Round to 2 decimal places
      finalPrice = Math.round(finalPrice * 100) / 100;

      setResult({
        price: finalPrice,
        estimatedDays: deliveryTimes[formData.zone],
        weight: weight,
        zone: formData.zone,
        baseCost: data.price,
        additionalCosts: finalPrice - data.price
      });

      toast.success('Price calculated successfully');
    } catch (err) {
      setError(err.message);
      toast.error(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    calculatePrice();
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(price);
  };

  return (
    <div className="max-w-2xl mx-auto p-6">
      <div className="bg-white rounded-lg shadow-sm p-6">
        {/* Header */}
        <div className="flex items-center space-x-3 mb-6">
          <Calculator className="h-8 w-8 text-blue-600" />
          <div>
            <h2 className="text-xl font-bold text-gray-900">Shipping Price Calculator</h2>
            <p className="text-sm text-gray-500">Get instant shipping cost estimates</p>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Weight Input */}
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Package Weight
            </label>
            <div className="mt-1 relative rounded-md shadow-sm">
              <input
                type="number"
                value={formData.weight}
                onChange={(e) => setFormData({...formData, weight: e.target.value})}
                step="0.01"
                min="0.01"
                max="100"
                className="block w-full pl-10 pr-12 py-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
                placeholder="0.00"
              />
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Package className="h-5 w-5 text-gray-400" />
              </div>
              <div className="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none">
                <span className="text-gray-500 sm:text-sm">kg</span>
              </div>
            </div>
            <p className="mt-1 text-xs text-gray-500">Weight range: 0.01 kg - 100 kg</p>
          </div>

          {/* Zone Selection */}
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Delivery Type
            </label>
            <select
              value={formData.zone}
              onChange={(e) => setFormData({...formData, zone: e.target.value})}
              className="mt-1 block w-full pl-3 pr-10 py-2 text-base border border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 rounded-lg"
            >
              <option value="default">Standard Delivery</option>
              <option value="express">Express Delivery</option>
              <option value="international">International Shipping</option>
            </select>
            <p className="mt-1 text-xs text-gray-500">
              Estimated delivery: {deliveryTimes[formData.zone]}
            </p>
          </div>

          {error && (
            <div className="rounded-md bg-red-50 p-4">
              <div className="flex items-center">
                <AlertCircle className="h-5 w-5 text-red-400" />
                <div className="ml-3">
                  <p className="text-sm text-red-700">{error}</p>
                </div>
              </div>
            </div>
          )}

          <button
            type="submit"
            disabled={loading}
            className={`w-full flex items-center justify-center px-4 py-2 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 ${
              loading ? 'opacity-75 cursor-not-allowed' : ''
            }`}
          >
            {loading ? (
              <>
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Calculating...
              </>
            ) : (
              <>
                <Calculator className="h-5 w-5 mr-2" />
                Calculate Price
              </>
            )}
          </button>
        </form>

        {/* Result Section */}
        {result && (
          <div className="mt-6 rounded-lg bg-green-50 p-6">
            <h3 className="text-lg font-medium text-green-900 flex items-center gap-2">
              <IndianRupee className="h-5 w-5" />
              Price Estimate
            </h3>
            <div className="mt-4 space-y-4">
              <div className="flex justify-between items-center border-b border-green-200 pb-4">
                <div className="space-y-1">
                  <p className="text-3xl font-bold text-green-700">
                    {formatPrice(result.price)}
                  </p>
                  <p className="text-sm text-green-600">
                    Total shipping cost
                  </p>
                </div>
                <div className="text-right">
                  <p className="text-sm font-medium text-green-700">
                    {result.weight} kg
                  </p>
                  <p className="text-sm text-green-600">
                    Package weight
                  </p>
                </div>
              </div>
              
              {result.additionalCosts > 0 && (
                <div className="flex justify-between text-sm text-green-600 border-b border-green-200 pb-2">
                  <span>Base Cost:</span>
                  <span>{formatPrice(result.baseCost)}</span>
                </div>
              )}

              <div className="flex items-start gap-3">
                <Clock className="h-5 w-5 text-green-700 mt-0.5" />
                <div>
                  <p className="text-sm font-medium text-green-700">
                    Estimated Delivery Time
                  </p>
                  <p className="text-sm text-green-600">
                    {result.estimatedDays}
                  </p>
                </div>
              </div>

              <div className="flex items-start gap-3 bg-green-100 p-3 rounded-md">
                <Info className="h-5 w-5 text-green-700 mt-0.5" />
                <div className="text-sm text-green-700">
                  <p>Price includes packaging and handling charges.</p>
                  {result.zone !== 'default' && (
                    <p>Additional charges applied for {result.zone} delivery.</p>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default PriceCalculator;
