// src/components/admin/dashboard/StatsOverview.jsx
import React, { useState, useEffect } from 'react';
import { 
  Package, TrendingUp, Users, IndianRupee, 
  Clock, AlertCircle
} from 'lucide-react';
import axios from 'axios';

// Reusable stat card component
const StatCard = ({ title, value, icon: Icon, description }) => (
  <div className="bg-white p-6 rounded-lg shadow-sm">
    <div className="flex justify-between items-start">
      <div>
        <p className="text-sm font-medium text-gray-600">{title}</p>
        <p className="text-2xl font-semibold text-gray-900 mt-2">{value}</p>
        {description && (
          <p className="mt-1 text-sm text-gray-500">{description}</p>
        )}
      </div>
      <div className="p-3 bg-blue-50 rounded-lg">
        <Icon className="h-6 w-6 text-blue-600" />
      </div>
    </div>
  </div>
);

const StatsOverview = () => {
  const [stats, setStats] = useState({
    totalCouriers: 0,
    activeShipments: 0,
    revenue: 0,
    avgDeliveryTime: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem('adminToken');
        
        const response = await axios.get('http://localhost:5000/api/admin/dashboard-stats', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        setStats(response.data);
      } catch (err) {
        console.error('Error fetching stats:', err);
        setError('Failed to load dashboard statistics');
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []); // Empty dependency array to run only once on mount

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {[1, 2, 3, 4].map((i) => (
          <div key={i} className="bg-white p-6 rounded-lg shadow-sm animate-pulse">
            <div className="h-4 bg-gray-200 rounded w-1/3"></div>
            <div className="mt-4 h-8 bg-gray-200 rounded"></div>
          </div>
        ))}
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 p-4 rounded-lg">
        <div className="flex items-center">
          <AlertCircle className="h-5 w-5 text-red-500 mr-2" />
          <span className="text-red-700">{error}</span>
        </div>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      <StatCard
        title="Total Shipments"
        value={stats.totalCouriers}
        icon={Package}
        description={`${stats.activeShipments} active`}
      />
      <StatCard
        title="Revenue"
        value={`â‚¹${stats.revenue.toLocaleString()}`}
        icon={IndianRupee}
        description="Total earnings"
      />
      <StatCard
        title="Success Rate"
        value={`${stats.successRate || 0}%`}
        icon={TrendingUp}
        description={`${stats.deliveredCount || 0} delivered`}
      />
      <StatCard
        title="Avg Delivery Time"
        value={`${Math.round(stats.avgDeliveryTime || 0)} hrs`}
        icon={Clock}
        description="Target: 72 hours"
      />
    </div>
  );
};

export default StatsOverview;
