import React, { useState } from 'react';
const DeliveryPerformance = ({ dateRange }) => {
    const [performance, setPerformance] = useState([]);
    const [loading, setLoading] = useState(true);
  
    useEffect(() => {
      const fetchPerformance = async () => {
        try {
          const token = localStorage.getItem('adminToken');
          const response = await axios.get('http://localhost:5000/api/admin/courier-details', {
            headers: { 'Authorization': `Bearer ${token}` }
          });
          setPerformance(response.data);
        } catch (error) {
          console.error('Error fetching performance:', error);
        } finally {
          setLoading(false);
        }
      };
  
      fetchPerformance();
    }, [dateRange]);
  
    if (loading) return <div className="animate-pulse">Loading...</div>;
  
    return (
      <div className="bg-white p-6 rounded-lg shadow-sm">
        <h2 className="text-lg font-semibold mb-4">Delivery Performance</h2>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead>
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Tracking ID
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Sender
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Location
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Last Update
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {performance.map((item) => (
                <tr key={item.c_id}>
                  <td className="px-6 py-4 whitespace-nowrap">{item.billno}</td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div>
                      <div className="text-sm font-medium text-gray-900">{item.sender_name}</div>
                      <div className="text-sm text-gray-500">{item.sender_email}</div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                      ${item.current_status === 'Delivered' ? 'bg-green-100 text-green-800' : 
                      item.current_status === 'In Transit' ? 'bg-blue-100 text-blue-800' : 
                      'bg-yellow-100 text-yellow-800'}`}>
                      {item.current_status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {item.current_location || 'N/A'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(item.status_update_time).toLocaleDateString()}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };
