import React from 'react';
import { useAdminDashboard } from '../../../hooks/useAdminDashboard';
import StatsOverview from './StatsOverview';
import RevenueChart from './RevenueChart';
import ActiveSenders from './ActiveSenders';
import { Card } from '../../../components/ui/card';

const AdminDashboard = () => {
  const { dashboardData, loading, error } = useAdminDashboard();

  if (loading) {
    return (
      <Card className="p-8">
        <div className="flex justify-center">Loading...</div>
      </Card>
    );
  }

  if (error) {
    return (
      <Card className="p-8 bg-red-50">
        <div className="text-red-600">{error}</div>
      </Card>
    );
  }

  return (
    <div className="space-y-6 p-6">
      <StatsOverview stats={dashboardData?.stats} />
      <div className="grid gap-6 md:grid-cols-2">
        <RevenueChart data={dashboardData?.revenue} />
        <ActiveSenders senders={dashboardData?.activeSenders} />
      </div>
    </div>
  );
};

export default AdminDashboard;
