// (Uses nested query from active_senders view)
const TopShippers = () => {
    const [shippers, setShippers] = useState([]);
    const [loading, setLoading] = useState(true);
  
    useEffect(() => {
      const fetchShippers = async () => {
        try {
          const token = localStorage.getItem('adminToken');
          const response = await axios.get('http://localhost:5000/api/admin/active-senders', {
            headers: { 'Authorization': `Bearer ${token}` }
          });
          setShippers(response.data);
        } catch (error) {
          console.error('Error fetching shippers:', error);
        } finally {
          setLoading(false);
        }
      };
  
      fetchShippers();
    }, []);
  
    if (loading) return <div className="animate-pulse">Loading...</div>;
  
    return (
      <div className="bg-white p-6 rounded-lg shadow-sm">
        <h2 className="text-lg font-semibold mb-4">Top Shippers</h2>
        <div className="space-y-4">
          {shippers.map((shipper) => (
            <div key={shipper.u_id} className="flex justify-between items-center">
              <div>
                <p className="font-medium">{shipper.name}</p>
                <p className="text-sm text-gray-500">{shipper.email}</p>
              </div>
              <div className="text-sm">
                <span className="font-semibold">{shipper.total_couriers}</span> shipments
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };
