// src/components/admin/dashboard/AdminDashboard.jsx
import React, { useState } from 'react';
import StatsOverview from './StatsOverview';
import TopShippers from './TopShippers';
import DeliveryPerformance from './DeliveryPerformance';
import RevenueOverview from './RevenueOverview';

const AdminDashboard = () => {
  const [dateRange, setDateRange] = useState('30');

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Dashboard</h1>
          <p className="mt-1 text-sm text-gray-500">
            Overview of your courier operations
          </p>
        </div>
        <select
          value={dateRange}
          onChange={(e) => setDateRange(e.target.value)}
          className="block pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm rounded-md"
        >
          <option value="7">Last 7 days</option>
          <option value="30">Last 30 days</option>
          <option value="90">Last 3 months</option>
        </select>
      </div>

      <StatsOverview dateRange={dateRange} />

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <RevenueOverview dateRange={dateRange} />
        <TopShippers />
      </div>

      <DeliveryPerformance dateRange={dateRange} />
    </div>
  );
};

export default AdminDashboard;
