import React from 'react';
import { NavLink } from 'react-router-dom';
import { 
  Home, Package, List, Search, 
  User, Bell, Calculator 
} from 'lucide-react';

const Sidebar = () => {
  // src/components/common/Sidebar.jsx
const navLinks = [
  {
    section: 'SHIPMENTS',
    links: [
      { name: 'Dashboard', to: '/dashboard', icon: Home },
      { name: 'New Shipment', to: '/create-courier', icon: Package },
      { name: 'My Shipments', to: '/shipments', icon: List },
      { name: 'Track Shipment', to: '/tracking', icon: Search },
      { name: 'Calculate Price', to: '/calculate-price', icon: Calculator }
    ]
  },
  {
    section: 'ACCOUNT',
    links: [
      { name: 'Profile', to: '/profile', icon: User },
      { name: 'Notifications', to: '/notifications', icon: Bell }
    ]
  }
];

  return (
    <div className="fixed left-0 top-16 w-64 h-full bg-white shadow-sm overflow-y-auto">
      <div className="p-4">
        {navLinks.map((section) => (
          <div key={section.section} className="mb-8">
            <h3 className="text-xs font-semibold text-gray-400 mb-4">
              {section.section}
            </h3>
            <nav className="space-y-1">
              {section.links.map((link) => {
                const Icon = link.icon;
                return (
                  <NavLink
                    key={link.to}
                    to={link.to}
                    className={({ isActive }) =>
                      `flex items-center px-4 py-2 text-sm font-medium rounded-md ${
                        isActive
                          ? 'bg-blue-50 text-blue-700'
                          : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
                      }`
                    }
                  >
                    <Icon className="h-5 w-5 mr-3" />
                    {link.name}
                  </NavLink>
                );
              })}
            </nav>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Sidebar;
