// src/components/admin/dashboard/StatsOverview.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Package, IndianRupee, TrendingUp, Clock } from 'lucide-react';

const StatCard = ({ title, value, subtitle, icon: IconComponent }) => (
  <div className="bg-white p-6 rounded-lg shadow-sm">
    <div className="flex items-center justify-between">
      <div>
        <p className="text-sm font-medium text-gray-500">{title}</p>
        <p className="text-2xl font-semibold text-gray-900 mt-2">{value}</p>
        {subtitle && (
          <p className="mt-1 text-sm text-gray-500">{subtitle}</p>
        )}
      </div>
      <div className="p-3 bg-blue-50 rounded-lg">
        <IconComponent className="h-6 w-6 text-blue-600" />
      </div>
    </div>
  </div>
);

const StatsOverview = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const token = localStorage.getItem('adminToken');
        const response = await axios.get('http://localhost:5000/api/admin/dashboard-stats', {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        setStats(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to fetch stats');
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {[1, 2, 3, 4].map((i) => (
          <div key={i} className="h-32 animate-pulse bg-gray-100 rounded-lg"></div>
        ))}
      </div>
    );
  }

  if (error) return <div className="text-red-500">{error}</div>;

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      <StatCard
        title="Total Shipments"
        value={stats?.totalShipments || 0}
        subtitle={`${stats?.activeShipments || 0} active`}
        icon={Package}
      />
      <StatCard
        title="Total Revenue"
        value={`₹${(stats?.totalRevenue || 0).toLocaleString()}`}
        subtitle={`₹${Math.round(stats?.avgRevenue || 0)} avg`}
        icon={IndianRupee}
      />
      <StatCard
        title="Success Rate"
        value={`${stats?.successRate.toFixed(1)}%`}
        subtitle={`${stats?.deliveredCount || 0} delivered`}
        icon={TrendingUp}
      />
      <StatCard
        title="Avg Delivery Time"
        value={`${Math.round(stats?.avgDeliveryTime || 0)} hrs`}
        subtitle="Target: 72 hours"
        icon={Clock}
      />
    </div>
  );
};

export default StatsOverview;
