// src/components/admin/dashboard/StatsOverview.jsx
// (Uses aggregate query from monthly_revenue view)
import React, { useState, useEffect } from 'react';
import { Package, IndianRupee, TrendingUp, Clock } from 'lucide-react';
import axios from 'axios';

const StatCard = ({ title, value, icon: IconComponent, change }) => (
  <div className="bg-white p-6 rounded-lg shadow-sm">
    <div className="flex justify-between items-start">
      <div>
        <p className="text-sm font-medium text-gray-600">{title}</p>
        <h3 className="text-2xl font-bold text-gray-900 mt-1">{value}</h3>
        {change && (
          <p className={`text-sm mt-1 ${change >= 0 ? 'text-green-600' : 'text-red-600'}`}>
            {change >= 0 ? '↑' : '↓'} {change}% from last month
          </p>
        )}
      </div>
      <div className="p-3 bg-blue-50 rounded-lg">
        <IconComponent className="h-6 w-6 text-blue-600" />
      </div>
    </div>
  </div>
);

const StatsOverview = ({ dateRange }) => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const token = localStorage.getItem('adminToken');
        const response = await axios.get('http://localhost:5000/api/admin/monthly-revenue', {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        setStats(response.data);
      } catch (error) {
        console.error('Error fetching stats:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, [dateRange]);

  if (loading) {
    return <div className="animate-pulse">Loading...</div>;
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      <StatCard
        title="Total Deliveries"
        value={stats?.total_deliveries || 0}
        icon={Package}
        change={stats?.delivery_growth}
      />
      <StatCard
        title="Revenue"
        value={`₹${(stats?.total_revenue || 0).toLocaleString()}`}
        icon={IndianRupee}
        change={stats?.revenue_growth}
      />
      <StatCard
        title="Success Rate"
        value={`${stats?.success_rate || 0}%`}
        icon={TrendingUp}
      />
      <StatCard
        title="Avg Price"
        value={`₹${Math.round(stats?.avg_price || 0)}`}
        icon={IndianRupee}
      />
    </div>
  );
};
