import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Package, User, Phone, MapPin, Mail, Scale } from 'lucide-react';
import { useAuth } from '../../hooks/useAuth';
import toast from 'react-hot-toast';
import courierService from '../../services/courierService';

const CreateCourierForm = () => {
  const [loading, setLoading] = useState(false);
  const [price, setPrice] = useState(null);
  const { user, token } = useAuth();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    sname: user?.name || '',
    semail: user?.email || '',
    sphone: user?.pnumber || '',
    saddress: user?.address || '',
    rname: '',
    remail: '',
    rphone: '',
    raddress: '',
    weight: '',
    image: null
  });

  // Handle weight change and calculate price
  useEffect(() => {
    const calculatePrice = async () => {
      if (formData.weight && !isNaN(formData.weight) && formData.weight > 0) {
        try {
          const result = await courierService.calculatePrice(formData.weight);
          setPrice(result.price);
        } catch (error) {
          console.error('Price calculation error:', error);
        }
      }
    };

    calculatePrice();
  }, [formData.weight]);

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'file' ? files[0] : value
    }));
  };

  const validateForm = () => {
    if (!formData.weight || formData.weight <= 0) {
      toast.error('Please enter a valid weight');
      return false;
    }
    
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.semail) || !emailRegex.test(formData.remail)) {
      toast.error('Please enter valid email addresses');
      return false;
    }

    const phoneRegex = /^\d{10}$/;
    if (!phoneRegex.test(formData.sphone) || !phoneRegex.test(formData.rphone)) {
      toast.error('Please enter valid 10-digit phone numbers');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);
    try {
      // Check authentication
      if (!token) {
        toast.error('Please login to create a shipment');
        navigate('/login');
        return;
      }

      // Create FormData object
      const formDataToSend = new FormData();
      
      // Append all form fields
      Object.keys(formData).forEach(key => {
        if (key === 'image' && formData[key]) {
          formDataToSend.append('image', formData[key]);
        } else if (formData[key] !== null && formData[key] !== '') {
          formDataToSend.append(key, formData[key]);
        }
      });

      // Add price if calculated
      if (price) {
        formDataToSend.append('price', price);
      }

      const response = await courierService.createCourier(formDataToSend);
      
      toast.success('Shipment created successfully! Tracking number: ' + response.courier.billno);
      navigate('/courier/' + response.courier.id);
    } catch (error) {
      console.error('Create shipment error:', error);
      toast.error(error.message || 'Failed to create shipment');
      
      if (error.message.includes('authentication')) {
        navigate('/login');
      }
    } finally {
      setLoading(false);
    }
  };

  // Price formatter
  const formatPrice = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(amount);
  };

  return (
    <div className="max-w-4xl mx-auto py-8 px-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-gray-900">Create New Shipment</h1>
        {price && (
          <div className="bg-blue-50 p-3 rounded-lg">
            <span className="text-sm text-blue-600 font-medium">Estimated Price:</span>
            <span className="ml-2 text-lg font-bold text-blue-700">{formatPrice(price)}</span>
          </div>
        )}
      </div>
      
      <form onSubmit={handleSubmit} className="space-y-8">
        {/* Rest of your form JSX remains the same */}
        {/* ... */}

        <div className="flex items-center justify-end space-x-4">
          <button
            type="button"
            onClick={() => navigate('/dashboard')}
            className="px-6 py-3 text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
          >
            Cancel
          </button>
          <button
            type="submit"
            disabled={loading}
            className={`px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 ${
              loading ? 'opacity-75 cursor-not-allowed' : ''
            }`}
          >
            {loading ? (
              <span className="flex items-center">
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Creating Shipment...
              </span>
            ) : (
              'Create Shipment'
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreateCourierForm;
