import React, { useState, useEffect } from 'react';
import { 
  TrendingUp, Clock, Package, Truck, 
  AlertCircle, Calendar, IndianRupee,
  Download, Filter 
} from 'lucide-react';
import {
  ResponsiveContainer,
  LineChart,
  BarChart,
  Line,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend
} from 'recharts';
import { adminService } from '../../../services/adminService';
import { useAuth } from '../../../hooks/useAuth';
import LoadingSpinner from '../../common/LoadingSpinner';

const MetricCard = ({ title, value, subvalue, icon: Icon, iconColor }) => (
  <div className="bg-white rounded-lg shadow-sm p-6">
    <div className="flex items-center justify-between">
      <h3 className="text-sm font-medium text-gray-500">{title}</h3>
      <Icon className={`h-5 w-5 text-${iconColor}`} />
    </div>
    <div className="mt-2">
      <p className="text-3xl font-bold text-gray-900">{value}</p>
      {subvalue && (
        <p className="mt-1 text-sm text-gray-500">{subvalue}</p>
      )}
    </div>
  </div>
);

const PerformanceMetrics = () => {
  const { getToken } = useAuth();
  const [metrics, setMetrics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [dateRange, setDateRange] = useState('30'); // days
  const [view, setView] = useState('overview'); // overview, delivery, revenue

  useEffect(() => {
    fetchMetrics();
  }, [dateRange]);

  const fetchMetrics = async () => {
    try {
      setLoading(true);
      const data = await adminService.getMetrics(dateRange);
      setMetrics(data);
      setError('');
    } catch (err) {
      console.error('Failed to fetch metrics:', err);
      setError(err.message || 'Failed to fetch metrics');
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async () => {
    try {
      // Implement export functionality
      console.log('Exporting data...');
    } catch (error) {
      console.error('Export error:', error);
      setError('Failed to export data');
    }
  };

  if (loading) return <LoadingSpinner />;

  if (error) {
    return (
      <div className="bg-red-50 p-4 rounded-md">
        <div className="flex items-center">
          <AlertCircle className="h-5 w-5 text-red-500 mr-2" />
          <span className="text-red-700">{error}</span>
        </div>
      </div>
    );
  }

  if (!metrics) return null;

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-900">Performance Metrics</h1>
        <div className="flex items-center gap-4">
          <select
            value={dateRange}
            onChange={(e) => setDateRange(e.target.value)}
            className="block w-40 pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm rounded-md"
          >
            <option value="7">Last 7 days</option>
            <option value="30">Last 30 days</option>
            <option value="90">Last 3 months</option>
            <option value="365">Last year</option>
          </select>
          <button
            onClick={handleExport}
            className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
          >
            <Download className="h-4 w-4 mr-2" />
            Export Report
          </button>
        </div>
      </div>

      {/* Metric Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <MetricCard
          title="Success Rate"
          value={`${metrics.successRate.toFixed(1)}%`}
          subvalue={`${metrics.deliveredCount} of ${metrics.totalShipments} delivered`}
          icon={TrendingUp}
          iconColor="green-500"
        />

        <MetricCard
          title="Avg Delivery Time"
          value={`${metrics.avgDeliveryTime.toFixed(1)} hrs`}
          subvalue="Target: 72 hours"
          icon={Clock}
          iconColor="blue-500"
        />

        <MetricCard
          title="Avg Revenue/Shipment"
          value={`₹${metrics.avgRevenue.toFixed(2)}`}
          subvalue={`Total: ₹${metrics.totalRevenue.toLocaleString()}`}
          icon={IndianRupee}
          iconColor="purple-500"
        />

        <MetricCard
          title="Failed Deliveries"
          value={metrics.failedCount}
          subvalue={`${((metrics.failedCount / metrics.totalShipments) * 100).toFixed(1)}% of total`}
          icon={AlertCircle}
          iconColor="red-500"
        />
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Delivery Performance Chart */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          <h3 className="text-lg font-medium text-gray-900 mb-6">
            Delivery Performance
          </h3>
          <div className="h-80">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={metrics.deliveryTrends}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line
                  type="monotone"
                  dataKey="successRate"
                  stroke="#10B981"
                  name="Success Rate"
                />
                <Line
                  type="monotone"
                  dataKey="avgDeliveryTime"
                  stroke="#6366F1"
                  name="Delivery Time"
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Revenue Trends Chart */}
        <div className="bg-white rounded-lg shadow-sm p-6">
          <h3 className="text-lg font-medium text-gray-900 mb-6">
            Revenue Trends
          </h3>
          <div className="h-80">
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={metrics.revenueTrends}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="revenue" fill="#8B5CF6" name="Revenue" />
                <Bar dataKey="shipments" fill="#60A5FA" name="Shipments" />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>

      {/* View Selector */}
      <div className="flex justify-center space-x-4">
        {['overview', 'delivery', 'revenue'].map((viewOption) => (
          <button
            key={viewOption}
            onClick={() => setView(viewOption)}
            className={`px-4 py-2 text-sm font-medium rounded-md ${
              view === viewOption
                ? 'bg-blue-50 text-blue-700'
                : 'text-gray-500 hover:text-gray-700'
            }`}
          >
            {viewOption.charAt(0).toUpperCase() + viewOption.slice(1)}
          </button>
        ))}
      </div>

      {/* Detailed Analysis Sections */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        {view === 'overview' && (
          <div className="space-y-6">
            {/* Add overview content */}
          </div>
        )}

        {view === 'delivery' && (
          <div className="space-y-6">
            {/* Add delivery content */}
          </div>
        )}

        {view === 'revenue' && (
          <div className="space-y-6">
            {/* Add revenue content */}
          </div>
        )}
      </div>
    </div>
  );
};

export default PerformanceMetrics;
