// src/components/admin/reports/PerformanceMetrics.jsx

import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Download } from 'lucide-react';
import { useAuth } from '../../../context/AuthContext';

const MetricCard = ({ title, value, description }) => (
  <div className="bg-white p-6 rounded-lg shadow">
    <h4 className="text-sm font-medium text-gray-600">{title}</h4>
    <div className="mt-2 flex items-baseline">
      <p className="text-2xl font-semibold text-gray-900">{value}</p>
      {description && (
        <p className="ml-2 text-sm text-gray-500">{description}</p>
      )}
    </div>
  </div>
);

const PerformanceMetrics = () => {
  const { user } = useAuth();
  const [metrics, setMetrics] = useState({
    deliverySuccessRate: 0,
    avgDeliveryTime: 0,
    customerSatisfaction: 0,
    totalDeliveries: 0,
    successfulDeliveries: 0,
    pendingDeliveries: 0,
    failedDeliveries: 0,
    timelineData: []
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [dateRange, setDateRange] = useState({
    start: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000),
    end: new Date()
  });

  useEffect(() => {
    const fetchMetrics = async () => {
      try {
        setLoading(true);
        const params = new URLSearchParams({
          start_date: dateRange.start.toISOString().split('T')[0],
          end_date: dateRange.end.toISOString().split('T')[0]
        });

        const response = await fetch(`/api/admin/metrics?${params}`, {
          headers: {
            'Authorization': `Bearer ${user.a_id}`
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch metrics');
        }

        const data = await response.json();
        setMetrics(data);
      } catch (error) {
        console.error('Error fetching metrics:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    if (user?.a_id) {
      fetchMetrics();
    }
  }, [dateRange, user]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 p-4 rounded-lg">
        <p className="text-red-600">Error: {error}</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-lg shadow">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-lg font-semibold text-gray-900">Performance Metrics</h3>
          <div className="flex items-center space-x-4">
            <div className="relative">
              <input
                type="date"
                className="px-4 py-2 border border-gray-300 rounded-md"
                value={dateRange.start.toISOString().split('T')[0]}
                onChange={(e) => setDateRange(prev => ({ 
                  ...prev, 
                  start: new Date(e.target.value) 
                }))}
              />
            </div>
            <div className="relative">
              <input
                type="date"
                className="px-4 py-2 border border-gray-300 rounded-md"
                value={dateRange.end.toISOString().split('T')[0]}
                onChange={(e) => setDateRange(prev => ({ 
                  ...prev, 
                  end: new Date(e.target.value) 
                }))}
              />
            </div>
            <button 
              className="flex items-center gap-2 px-4 py-2 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
            >
              <Download className="h-4 w-4" />
              Export Report
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <MetricCard
            title="Delivery Success Rate"
            value={`${metrics.deliverySuccessRate}%`}
            description="On-time deliveries"
          />
          <MetricCard
            title="Average Delivery Time"
            value={`${metrics.avgDeliveryTime}h`}
            description="From pickup to delivery"
          />
          <MetricCard
            title="Customer Satisfaction"
            value={`${metrics.customerSatisfaction}%`}
            description="Based on feedback"
          />
        </div>

        {metrics.timelineData && metrics.timelineData.length > 0 && (
          <div className="h-[400px]">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={metrics.timelineData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis yAxisId="left" orientation="left" stroke="#2563eb" />
                <YAxis yAxisId="right" orientation="right" stroke="#dc2626" />
                <Tooltip />
                <Legend />
                <Line
                  yAxisId="left"
                  type="monotone"
                  dataKey="successRate"
                  stroke="#2563eb"
                  name="Success Rate"
                />
                <Line
                  yAxisId="right"
                  type="monotone"
                  dataKey="deliveryTime"
                  stroke="#dc2626"
                  name="Delivery Time"
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        )}

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mt-6">
          <div className="p-4 bg-blue-50 rounded-lg">
            <p className="text-sm text-blue-600 font-medium">Total Deliveries</p>
            <p className="text-2xl font-bold text-blue-900">{metrics.totalDeliveries}</p>
          </div>
          <div className="p-4 bg-green-50 rounded-lg">
            <p className="text-sm text-green-600 font-medium">Successful Deliveries</p>
            <p className="text-2xl font-bold text-green-900">{metrics.successfulDeliveries}</p>
          </div>
          <div className="p-4 bg-yellow-50 rounded-lg">
            <p className="text-sm text-yellow-600 font-medium">Pending Deliveries</p>
            <p className="text-2xl font-bold text-yellow-900">{metrics.pendingDeliveries}</p>
          </div>
          <div className="p-4 bg-red-50 rounded-lg">
            <p className="text-sm text-red-600 font-medium">Failed Deliveries</p>
            <p className="text-2xl font-bold text-red-900">{metrics.failedDeliveries}</p>
          </div>
        </div>

        {/* Additional Analysis Section */}
        <div className="mt-8 pt-6 border-t border-gray-200">
          <h4 className="text-sm font-medium text-gray-600 mb-4">Performance Analysis</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-gray-50 p-4 rounded-lg">
              <h5 className="text-sm font-medium text-gray-700 mb-2">Success Metrics</h5>
              <ul className="space-y-2 text-sm">
                <li className="flex justify-between">
                  <span className="text-gray-600">On-time Delivery Rate:</span>
                  <span className="font-medium text-gray-900">
                    {metrics.deliverySuccessRate}%
                  </span>
                </li>
                <li className="flex justify-between">
                  <span className="text-gray-600">Average Delays:</span>
                  <span className="font-medium text-gray-900">
                    {Math.round((metrics.avgDeliveryTime / 24) * 10) / 10} days
                  </span>
                </li>
                <li className="flex justify-between">
                  <span className="text-gray-600">Completion Rate:</span>
                  <span className="font-medium text-gray-900">
                    {metrics.totalDeliveries ? 
                      Math.round((metrics.successfulDeliveries / metrics.totalDeliveries) * 100) 
                      : 0}%
                  </span>
                </li>
              </ul>
            </div>
            <div className="bg-gray-50 p-4 rounded-lg">
              <h5 className="text-sm font-medium text-gray-700 mb-2">Areas for Improvement</h5>
              <ul className="space-y-2 text-sm">
                <li className="flex justify-between">
                  <span className="text-gray-600">Failed Deliveries:</span>
                  <span className="font-medium text-gray-900">
                    {metrics.failedDeliveries} ({metrics.totalDeliveries ? 
                      Math.round((metrics.failedDeliveries / metrics.totalDeliveries) * 100) 
                      : 0}%)
                  </span>
                </li>
                <li className="flex justify-between">
                  <span className="text-gray-600">Pending Rate:</span>
                  <span className="font-medium text-gray-900">
                    {metrics.totalDeliveries ? 
                      Math.round((metrics.pendingDeliveries / metrics.totalDeliveries) * 100) 
                      : 0}%
                  </span>
                </li>
                <li className="flex justify-between">
                  <span className="text-gray-600">Average Resolution Time:</span>
                  <span className="font-medium text-gray-900">
                    {metrics.avgDeliveryTime}h
                  </span>
                </li>
              </ul>
            </div>
          </div>
        </div>

        {/* Trend Analysis */}
        <div className="mt-8 pt-6 border-t border-gray-200">
          <h4 className="text-sm font-medium text-gray-600 mb-4">Trend Analysis</h4>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="bg-blue-50 p-4 rounded-lg">
              <h5 className="text-sm font-medium text-blue-700 mb-2">Delivery Efficiency</h5>
              <div className="text-2xl font-bold text-blue-900">
                {metrics.avgDeliveryTime < 24 ? '✓ Good' : '⚠️ Needs Improvement'}
              </div>
              <p className="text-sm text-blue-600 mt-1">
                {metrics.avgDeliveryTime < 24 
                  ? 'Deliveries are being completed within target timeframe' 
                  : 'Deliveries are taking longer than expected'}
              </p>
            </div>
            <div className="bg-green-50 p-4 rounded-lg">
              <h5 className="text-sm font-medium text-green-700 mb-2">Success Rate Trend</h5>
              <div className="text-2xl font-bold text-green-900">
                {metrics.deliverySuccessRate > 90 ? '✓ Excellent' : 
                 metrics.deliverySuccessRate > 80 ? '⚠️ Good' : '⚠️ Needs Attention'}
              </div>
              <p className="text-sm text-green-600 mt-1">
                {metrics.deliverySuccessRate > 90 
                  ? 'Maintaining high success rate' 
                  : 'Room for improvement in success rate'}
              </p>
            </div>
            <div className="bg-purple-50 p-4 rounded-lg">
              <h5 className="text-sm font-medium text-purple-700 mb-2">Customer Satisfaction</h5>
              <div className="text-2xl font-bold text-purple-900">
                {metrics.customerSatisfaction > 85 ? '✓ High' : '⚠️ Moderate'}
              </div>
              <p className="text-sm text-purple-600 mt-1">
                Based on delivery success and timing metrics
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PerformanceMetrics;
