import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search, Bell, LogOut, Plus } from 'lucide-react';
import { useAuth } from '../../hooks/useAuth';

const Navbar = () => {
  const [trackingNo, setTrackingNo] = useState('');
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    if (trackingNo.trim()) {
      navigate(`/tracking/${trackingNo.trim()}`);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="fixed top-0 left-0 right-0 h-16 bg-white shadow-sm z-50">
      <div className="h-full px-4 flex items-center justify-between">
        {/* Logo */}
        <Link to="/" className="flex items-center space-x-2">
          <Package className="h-8 w-8 text-blue-600" />
          <span className="text-xl font-bold">FastCourier</span>
        </Link>

        {/* Search Bar */}
        <form onSubmit={handleSearch} className="max-w-xl w-full mx-4">
          <div className="relative">
            <input
              type="text"
              placeholder="Track your shipment"
              value={trackingNo}
              onChange={(e) => setTrackingNo(e.target.value)}
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
            />
            <Search className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
            <button
              type="submit"
              className="absolute right-2 top-2 px-3 py-1 bg-blue-600 text-white rounded-md"
            >
              Track
            </button>
          </div>
        </form>

        {/* Right Section */}
        <div className="flex items-center space-x-4">
          <button
            onClick={() => navigate('/create-courier')}
            className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-md"
          >
            <Plus className="h-5 w-5 mr-2" />
            New Shipment
          </button>

          <button
            onClick={() => navigate('/notifications')}
            className="relative p-2 text-gray-500 hover:text-gray-700"
          >
            <Bell className="h-6 w-6" />
            {/* Notification badge */}
            <span className="absolute top-0 right-0 h-4 w-4 bg-red-500 text-white text-xs rounded-full flex items-center justify-center">
              3
            </span>
          </button>

          {/* User Menu */}
          <div className="relative">
            <div className="flex items-center space-x-3">
              <img
                src={user?.profile_image || '/default-avatar.png'}
                alt="Profile"
                className="h-8 w-8 rounded-full"
              />
              <span className="text-sm font-medium">{user?.name}</span>
            </div>

            {/* Dropdown Menu */}
            <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg py-1">
              <Link
                to="/profile"
                className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100"
              >
                Profile
              </Link>
              <button
                onClick={handleLogout}
                className="w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-gray-100"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Navbar;
